import React, { useState, useEffect } from 'react';
import Select from 'react-select';

import DateRangePicker from 'react-bootstrap-daterangepicker';
import axios from 'axios';

import {
    Badge,
    Button,
    Card,
    Navbar,
    Nav,
    Table,
    Container,
    Row,
    Col,
    Form,
    OverlayTrigger,
    Tooltip,
  } from "react-bootstrap";




  const customStyles = {
    option: (provided, state) => ({
      ...provided,
      borderBottom: '2px dotted green',
      color: state.isSelected ? 'yellow' : 'black',
      backgroundColor: state.isSelected ? 'green' : 'white'
    }),
    control: (provided) => ({
      ...provided,
      marginTop: "5%",
    })
  };



  const getNotes = () => {
    return new Promise (
      (resolve, reject) => 
     fetch('https://jsonplaceholder.typicode.com/todos')
     .then(data => resolve(data.json()))
     .catch(err => reject(err))
     )  
  }

  
  function Dropdown() {
    const [showForm, setShowForm] = useState('false');
    const [options, setOptions] = useState('');
    const [data, setData] = useState({ hits: [] });

    let comp;

    const handleAddrTypeChange = (e) => 
  { 
    setShowForm((e.value)); 

  }
  
  useEffect(() => {
    getNotes().then( data => setToDos(data))

    });





  if (showForm == 'Region') {
  
     
    comp =  <Select className="col-md-2 col-offset-4" styles = { customStyles } options = {options} />
  
  }
  else if(showForm == 'Dealer')
  {
    comp = <Select className="col-md-2 col-offset-4"
    styles = { customStyles }
    options = {options} />
  }


    return (
        <>

<ul>
      {data.hits.map(item => (
        <li key={item.objectID}>
          <a href={item.url}>{item.title}</a>
        </li>
      ))}
    </ul>
        <Container fluid >
        <Row style={{margin:'1px'}}>

        <DateRangePicker     styles = { customStyles }
 >
        <input id = "someRandomID" className="col-sm-2 col-offset-4"  style={{    height: '40px', margin: '10px'}}/>
      </DateRangePicker>
    
     <Select className="col-sm-2 col-offset-4"
     styles = { customStyles }
     defaultValue={{ value: 'National', label: 'National' }}
  options = {options} 
  onChange={e => handleAddrTypeChange(e)}


  
  />







{comp}


</Row>
</Container>
</>
      );
    }
  
  
  export default Dropdown;
