import React , {useEffect} from "react";
import ChartistGraph from "react-chartist";
import ChartistTooltip from 'chartist-plugin-tooltips-updated';
import Chartist from "react-chartist";

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Navbar,
  Nav,
  Table,
  Container,
  Row,
  Col,
  Form,
  OverlayTrigger,
  Tooltip,
} from "react-bootstrap";




const data1 = {
  labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'],  
  series: [     [20000, 30000, 35000, 32000, 40000, 42000, 50000, 62000, 80000, 94000, 100000, 120000]],

};



var options1 = {

  showArea: false,
  fullWidth: true,
  lineSmooth: false,
  chartPadding: {
    right: 20,
    left: 10
  },
  axisX: {
    showGrid: true,
    labelInterpolationFnc: function(value) {
      return value.split('').slice(0, 3).join('');
    },
  },

  lineSmooth: true,
  showLine: true,
  showPoint: true,
  fullWidth: true,
 


     
}

                  

const plugins = [
  ChartistTooltip()
]

function Dashboard(page) {


  {
    page.todos1.national_vs_regional_chart.datasets.map(todo2 => 

     {let todo2s = (todo2.data)}  

    )
  }
 for(var i=0; i<page.todos1.national_vs_regional_chart.datasets.length; i++)
 {
   var series = page.todos1.national_vs_regional_chart.datasets[i];


var data = {
  labels: page.todos1.national_vs_regional_chart.labels,  
  series:[todo2s],
}
}



var options = {

                      showArea: false,
                      fullWidth: true,
                      lineSmooth: false,
                      chartPadding: {
                        right: 20,
                        left: 10
                      },
                      axisX: {
                        showGrid: true,
                        labelInterpolationFnc: function(value) {
                          return value.split('').slice(0, 3).join('');
                        },
                      },
                    
                      lineSmooth: true,
                      showLine: true,
                      showPoint: true,
                      fullWidth: true,
                     
                    
                    
                         
}


  return (
    <>

<Container fluid >

        <Row >

        {
        page.todos1.score_tiles_data.map(todo => 
            <Col lg="2" sm="6">
            <Card className="card-stats">
              <Card.Body>
                <Row>
                  <Col xs="12">
                  <Card.Title as="h4" style={{textAlign:'left',color: '#FF439B'}}>{todo.value}</Card.Title>

<Card.Title as="h4" style={{textAlign:'left',color:'#5B5E61'}}>{todo.display_header}</Card.Title>

                  </Col>
                  <Col xs="6">
                   
                  </Col>
                 
                </Row>
              </Card.Body>
             
            </Card>
          </Col>
          )
}
         
        </Row>

        </Container>

      <Container fluid>
       
        <Row>
          <Col md="8">
            <Card style={{height:'480px'}}>
              <Card.Header>
                <Card.Title as="h4">Day wise Usage</Card.Title>
              </Card.Header>
              <Card.Body>
                <div className="ct-chart" id="chartHours">
                <Chartist data={data} options={{...options, plugins}} type="Bar" className="ct-series-g ct-major-tenth" />

                </div>
              </Card.Body>
             
            </Card>
          </Col>
          <Col md="4">
            <Card style={{height:'480px'}}>
              <Card.Header>
                <Card.Title as="h4">Top Viewing Products</Card.Title>
              </Card.Header>
              <Card.Body>
                <div
                  className="ct-chart ct-perfect-fourth"
                  id="chartPreferences"
                >
                  <ChartistGraph
                    data={{
                      labels: ["40%", "20%", "40%"],
                      series: [40, 20, 40],
                    }}
                    type="Pie"
                  />
                </div>
               
               
              </Card.Body>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md="8">
            <Card style={{height:'480px'}}>
              <Card.Header>
                <Card.Title as="h4">Day wise Usage</Card.Title>
              </Card.Header>
              <Card.Body>
                <div className="ct-chart" id="chartHours">
                <Chartist data={data1} options={{...options1, plugins}} type="Bar" className="ct-series-g ct-major-tenth" />

                </div>
              </Card.Body>
             
            </Card>
          </Col>
          <Col md="4">
            <Card style={{height:'480px'}}>
              <Card.Header>
                <Card.Title as="h4">Top Viewing Products</Card.Title>
              </Card.Header>
              <Card.Body>
                <div
                  className="ct-chart ct-perfect-fourth"
                  id="chartPreferences"
                >
                  <ChartistGraph
                    data={{
                      labels: ["40%", "20%", "40%"],
                      series: [40, 20, 40],
                    }}
                    type="Pie"
                  />
                </div>
               
               
              </Card.Body>
            </Card>
          </Col>


         
        </Row>
       
      </Container>



    </>
  );
}

export default Dashboard;
