import React,{useState} from 'react';
import Select from 'react-select';

import DateRangePicker from 'react-bootstrap-daterangepicker';
import axios from 'axios';

import {
    Badge,
    Button,
    Card,
    Navbar,
    Nav,
    Table,
    Container,
    Row,
    Col,
    Form,
    OverlayTrigger,
    Tooltip,
  } from "react-bootstrap";






  const customStyles = {
    option: (provided, state) => ({
      ...provided,
      borderBottom: '2px dotted green',
      color: state.isSelected ? 'yellow' : 'black',
      backgroundColor: state.isSelected ? 'green' : 'white'
    }),
    control: (provided) => ({
      ...provided,
      marginTop: "5%",
    })
  };


  componentDidMount() 
  {
    axios.get('https://api.npms.io/v2/invalid-url')
        .then(response => this.setState({ totalReactPackages: response.data.total }))
        .catch(error => {
            this.setState({ options: error.message });
            console.error('There was an error!', error);
        });
}

 
  function Dropdown() {
    const [showForm, setShowForm] = useState('false');
    const [options, setOptions] = useState('');

    let comp;

    const handleAddrTypeChange = (e) => 
  { 
    setShowForm((e.value)); 

  }

  const buttons = '';
  if (showForm == 'Region') {
    comp =  <Select className="col-md-2 col-offset-4" styles = { customStyles } options = {options} />;
  }
  else if(showForm == 'Dealer')
  {
    comp = <Select className="col-md-2 col-offset-4"
    styles = { customStyles }
    options = {options} />
  }


    return (
        <>
        <Container fluid >
        <Row style={{margin:'1px'}}>

        <DateRangePicker     styles = { customStyles }
 >
        <input id = "someRandomID" className="col-sm-2 col-offset-4"  style={{    height: '40px', margin: '10px'}}/>
      </DateRangePicker>
    
     <Select className="col-sm-2 col-offset-4"
     styles = { customStyles }
     defaultValue={{ value: 'National', label: 'National' }}
  options = {options} 
  onChange={e => handleAddrTypeChange(e)}


  
  />


{comp}


</Row>
</Container>
</>
      );
    }
  
  
  export default Dropdown;
