import React,{useState} from 'react';
import Select from 'react-select';
import { DateRangePicker } from 'react-date-range';
import { addDays } from 'date-fns';

import {
    Badge,
    Button,
    Card,
    Navbar,
    Nav,
    Table,
    Container,
    Row,
    Col,
    Form,
    OverlayTrigger,
    Tooltip,
  } from "react-bootstrap";

const options = [
    { value: 'blues', label: 'Blues' },
    { value: 'rock', label: 'Rock' },
    { value: 'jazz', label: 'Jazz' },
    { value: 'orchestra', label: 'Orchestra' } 
  ];

  const customStyles = {
    option: (provided, state) => ({
      ...provided,
      borderBottom: '2px dotted green',
      color: state.isSelected ? 'yellow' : 'black',
      backgroundColor: state.isSelected ? 'green' : 'white'
    }),
    control: (provided) => ({
      ...provided,
      marginTop: "5%",
    })
  }

  const renderStaticRangeLabel = () => (
    <CustomStaticRangeLabelContent text={'This is a custom label content: '} />
  );

  class CustomStaticRangeLabelContent extends React.Component {
    constructor(props) {
      super(props);
  
      this.state = {
        currentDateString: Date(),
      };
  
      this.intervalId = setInterval(() => {
        this.setState({
          currentDateString: Date(),
        });
      }, 1000);
    }
  
    componentWillUnmount() {
      if (this.intervalId) {
        clearInterval(this.intervalId);
      }
    }
  
    render() {
      const { currentDateString } = this.state;
      const { text } = this.props;
  
      return (
        <span>
          <i>{text}</i>
          <span className={'random-date-string'}>
            <b>{currentDateString}</b>
          </span>
        </span>
      );
    }
  }
  

 
  function Dropdown() {


    const [state, setState] = useState([
        {
          startDate: new Date(),
          endDate: addDays(new Date(), 7),
          key: 'selection'
        }
      ]);


   
    return (
        <>
        <Container fluid >
        <Row style={{margin:'1px'}}>
        <DateRangePicker className="col-sm-4 col-offset-4" 
  onChange={item => setState([item.selection])}

  renderStaticRangeLabel={renderStaticRangeLabel}
  staticRanges={[
    {
      label: 'Hoy',
      hasCustomRendering: true,
      range: () => ({
        startDate: new Date(),
        endDate: new Date()
      }),
      isSelected() {
        return true;
      }
    }
  ]}

      />
     <Select className="col-sm-2 col-offset-4"
     styles = { customStyles }
  options = {options} />
  <Select className="col-md-2 col-offset-4"
  styles = { customStyles }
options = {options} />
<Select className="col-md-2 col-offset-4"
styles = { customStyles }
options = {options} />
</Row>
</Container>
</>
      );
    }
  
  
  export default Dropdown;
