import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import { trackPromise } from 'react-promise-tracker';
import $ from 'jquery';

import DateRangePicker from 'react-bootstrap-daterangepicker';
import axios from 'axios';

import {
    Badge,
    Button,
    Card,
    Navbar,
    Nav,
    Table,
    Container,
    Row,
    Col,
    Form,
    OverlayTrigger,
    Tooltip,
  } from "react-bootstrap";



 

  const customStyles = {
    option: (provided, state) => ({
      ...provided,
      borderBottom: '2px dotted green',
      color: state.isSelected ? 'yellow' : 'black',
      backgroundColor: state.isSelected ? 'green' : 'white'
    }),
    control: (provided) => ({
      ...provided,
      marginTop: "5%",
    })
  };



  const options1 = [
    { value: 'National', label: 'National' },
    { value: 'Region', label: 'Region' },
    { value: 'Dealer', label: 'Dealer' }
  ]

  
  function Dropdown() {
    const [showForm, setShowForm] = useState('false');
    const [isLoading, setIsLoading] = useState(false);
    const [resp, setGitData] = useState({ data: null, repos: null });
    const [selectValue, setSelectValue] = useState('');

    let comp , comp1;

    const handleAddrTypeChange = (e) => 
  { 
    setShowForm((e.value)); 

  }

  const handleAddrTypeChange1 = (e) => 
  { 
    setSelectValue((e.value)); 

  }


  useEffect(() => {

    const fetchData = async () => {

      setIsLoading(true);

      const respGlobal = await axios(
        `https://kmi-salessatisfaction.apprikart.com/secured_api/dashboard_api/get_dealer_list/?username=login1&permission=[%22KIA%22]&start_date=01/01/2021&end_date=30/01/2021&report_type=DCSI`
      );
      const respRepos = await axios(
        `https://kmi-salessatisfaction.apprikart.com/secured_api/dashboard_api/get_region_list/?username=login1&permission=[%22KIA%22]&start_date=01/01/2021&end_date=30/01/2021&report_type=DCSI`
      );

      setGitData({ data: respGlobal.data, repos: respRepos.data });
      setIsLoading(false);

    };



    trackPromise(
      fetchData()
    );

    }, []);

let defaultValue = resp.repos[0]

  if (showForm == 'Region') {
  

    comp =  <Select className="col-md-2 col-offset-4" name="anyCustomName" styles = { customStyles } options = {resp.repos}  value={defaultValue}
    onChange={e => handleAddrTypeChange1(e)}
    />

  }
  else if(showForm == 'Dealer')
  {
    
    comp1 = <Select className="col-md-2 col-offset-4"
    styles = { customStyles }
    options = {resp.data} 
    defaultValue={resp.data[0]}

/>
  }

  const hiddenInputValue = $( "input[name='anyCustomName']" ).val();
    return (
        <>
{hiddenInputValue}

        <Container fluid >
        <Row style={{margin:'1px'}}>

        <DateRangePicker     styles = { customStyles }
 >
        <input id = "someRandomID" className="col-sm-2 col-offset-4"  style={{    height: '40px', margin: '10px'}}/>
      </DateRangePicker>
    
     <Select className="col-sm-2 col-offset-4"
     styles = { customStyles }
     defaultValue={{ value: 'National', label: 'National' }}
  options = {options1} 
  onChange={e => handleAddrTypeChange(e)}


  
  />







{comp}
{comp1}

</Row>
</Container>
</>
      );
    }
  
  
  export default Dropdown;
