import React,{useState} from 'react';
import Select from 'react-select';

import DateRangePicker from 'react-bootstrap-daterangepicker';

import {
    Badge,
    Button,
    Card,
    Navbar,
    Nav,
    Table,
    Container,
    Row,
    Col,
    Form,
    OverlayTrigger,
    Tooltip,
  } from "react-bootstrap";

const options = [
    { value: 'National', label: 'National' },
    { value: 'Region', label: 'Region' },
    { value: 'Dealer', label: 'Dealer' }
  ];

  const customStyles = {
    option: (provided, state) => ({
      ...provided,
      borderBottom: '2px dotted green',
      color: state.isSelected ? 'yellow' : 'black',
      backgroundColor: state.isSelected ? 'green' : 'white'
    }),
    control: (provided) => ({
      ...provided,
      marginTop: "5%",
    })
  }


 

 
  function Dropdown() {
    const [showForm, setShowForm] = useState('false');

    const handleAddrTypeChange = (e) => 
  { 
    setShowForm((e.value)); 

  }

  if (showForm == 'Region') {
    const buttons = <Select className="col-md-2 col-offset-4" styles = { customStyles } options = {options} />;
  }


    return (
        <>
        <Container fluid >
        <Row style={{margin:'1px'}}>
        {showForm}

        <DateRangePicker     styles = { customStyles }
 >
        <input id = "someRandomID" className="col-sm-2 col-offset-4"  style={{    height: '40px', margin: '10px'}}/>
      </DateRangePicker>
    
     <Select className="col-sm-2 col-offset-4"
     styles = { customStyles }
     defaultValue={{ value: 'National', label: 'National' }}
  options = {options} 
  onChange={e => handleAddrTypeChange(e)}


  
  />

{button}




<Select className="col-md-2 col-offset-4"
styles = { customStyles }
options = {options} />
</Row>
</Container>
</>
      );
    }
  
  
  export default Dropdown;
