import React from "react";
import ChartistGraph from "react-chartist";
import ChartistTooltip from 'chartist-plugin-tooltips-updated';
import Chartist from "react-chartist";

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Navbar,
  Nav,
  Table,
  Container,
  Row,
  Col,
  Form,
  OverlayTrigger,
  Tooltip,
} from "react-bootstrap";



const data = {
  labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun','sun1'],
  series: [                        [287, 385, 490, 492, 554, 586, 698, 695]],

};

var options = {
  low: 0,
  high :800,
                      showArea: false,
                      fullWidth: true,
                      lineSmooth: false,
                      chartPadding: {
                        right: 20,
                        left: 10
                      },
                      axisX: {
                        showGrid: true,
                        labelInterpolationFnc: function(value) {
                          return value.split('').slice(0, 3).join('');
                        },
                      },
                    
                      lineSmooth: true,
                      showLine: true,
                      showPoint: true,
                      fullWidth: true,
                     
                    
                    
                         
                    }

                  

const plugins = [
  ChartistTooltip()
]

function Dashboard() {
  return (
    <>
      <Container fluid>
       
        <Row>
          <Col md="8">
            <Card style={{height:'500px'}}>
              <Card.Header>
                <Card.Title as="h4">Day wise Usage</Card.Title>
              </Card.Header>
              <Card.Body>
                <div className="ct-chart" id="chartHours">
                <Chartist data={data} options={{...options, plugins}} type="Line" className="ct-series-g ct-major-tenth" />

                </div>
              </Card.Body>
             
            </Card>
          </Col>
          <Col md="4">
            <Card style={{height:'500px'}}>
              <Card.Header>
                <Card.Title as="h4">Email Statistics</Card.Title>
              </Card.Header>
              <Card.Body>
                <div
                  className="ct-chart ct-perfect-fourth"
                  id="chartPreferences"
                >
                  <ChartistGraph
                    data={{
                      labels: ["40%", "20%", "40%"],
                      series: [40, 20, 40],
                    }}
                    type="Pie"
                  />
                </div>
               
               
              </Card.Body>
            </Card>
          </Col>
        </Row>
       
      </Container>
    </>
  );
}

export default Dashboard;
