import React from "react";
import ChartistGraph from "react-chartist";
import Chartist from "react-chartist";

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Navbar,
  Nav,
  Table,
  Container,
  Row,
  Col,
  Form,
  OverlayTrigger,
  Tooltip,
} from "react-bootstrap";



const data = {
  labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'],  
  series: [     [20000, 30000, 35000, 32000, 40000, 42000, 50000, 62000, 80000, 94000, 100000, 120000]],

};

var options = {

                      showArea: false,
                      fullWidth: true,
                      lineSmooth: false,
                      chartPadding: {
                        right: 20,
                        left: 10
                      },
                      axisX: {
                        showGrid: true,
                        labelInterpolationFnc: function(value) {
                          return value.split('').slice(0, 3).join('');
                        },
                      },
                    
                      lineSmooth: true,
                      showLine: true,
                      showPoint: true,
                      fullWidth: true,
                     
                    
                    
                         
                    }

          

function Dashboard() {
  return (
    <>
      <Container fluid>
       
        <Row>
          <Col md="8">
            <Card style={{height:'480px'}}>
              <Card.Header>
                <Card.Title as="h4">Day wise Usage</Card.Title>
              </Card.Header>
              <Card.Body>
                <div className="ct-chart" id="chartHours">
                <Chartist data={data} options={{...options}} type="Line" className="ct-series-g ct-major-tenth" />

                </div>
              </Card.Body>
             
            </Card>
          </Col>
          <Col md="4">
            <Card style={{height:'480px'}}>
              <Card.Header>
                <Card.Title as="h4">Top Viewing Products</Card.Title>
              </Card.Header>
              <Card.Body>
                <div
                  className="ct-chart ct-perfect-fourth"
                  id="chartPreferences"
                >
                  <ChartistGraph
                    data={{
                      labels: ["40%", "20%", "40%"],
                      series: [40, 20, 40],
                    }}
                    type="Pie"
                  />
                </div>
               
               
              </Card.Body>
            </Card>
          </Col>
        </Row>
       
      </Container>
    </>
  );
}

export default Dashboard;
