import React from 'react';
import Select from 'react-select';
// react-bootstrap components
import {
    Badge,
    Button,
    Card,
    Navbar,
    Nav,
    Table,
    Container,
    Row,
    Col,
    Form,
    OverlayTrigger,
    Tooltip,
  } from "react-bootstrap";

const options = [
    { value: 'blues', label: 'Blues' },
    { value: 'rock', label: 'Rock' },
    { value: 'jazz', label: 'Jazz' },
    { value: 'orchestra', label: 'Orchestra' } 
  ];

  const customStyles = {
    option: (provided, state) => ({
      ...provided,
      borderBottom: '2px dotted green',
      color: state.isSelected ? 'yellow' : 'black',
      backgroundColor: state.isSelected ? 'green' : 'white'
    }),
    control: (provided) => ({
      ...provided,
      marginTop: "5%",
    })
  }
  
  function Dropdown() {
   
    return (
        <>
        <Container fluid style={{margin:'10px'}}>
        <Row >
        <Select className="col-sm-2 col-offset-4"
        styles = { customStyles }
     options = {options} />
     <Select className="col-sm-2 col-offset-4"
     styles = { customStyles }
  options = {options} />
  <Select className="col-md-2 col-offset-4"
  styles = { customStyles }
options = {options} />
<Select className="col-md-2 col-offset-4"
styles = { customStyles }
options = {options} />
</Row>
</Container>
</>
      );
    }
  
  
  export default Dropdown;
